import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState("");

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });

  const titleChangHandler = (event) => {
    setEnteredTitle(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredTitle: event.target.value,

    // })
    // setUserInput((prevState) => {
    //   return {
    //     ...prevState,
    //     enteredTitle: event.target.value,
    //   };
    // });
  };

  const AmountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value,
    // });
    // setUserInput((prevState) => {
    //   return {
    //     ...prevState,
    //     enteredAmount: event.target.value
    //   }
    // })

  };

  const DatechangeHandler = (event) => {
    setEnteredDate(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value,
    // });

    // setUserInput((prevState) => {
    //   return {
    //     ...prevState,
    //     enteredDate: event.target.value
    //   }
    // })
  };

  // const inputChangeHandler = (identifier, value) => {
  //   if(identifier === "title") {
  //     setEnteredTitle(value);
  //   } else if (identifier === "date") {
  //     setEnteredDate(value);
  //   } else {
  //     setEnteredAmount(value)
  //   }
  // }

  const submitHandler = (event) => {
    event.preventDefault();
    //Prevents the page from being reloaded

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    }

    props.onSaveExpenseData(expenseData)
    setEnteredTitle("") 
    setEnteredAmount("")
    setEnteredDate("")
  };

  // const [isClosed, setIsOpening] = useState(false)
  // const [buttonVisible, setButtonVisible] = useState(true)
  // const [ExpenseButtonVisible, setExpenseButtonUnvisible] = useState(false)

  // const buttonOpeningForm = () => {
  //   setIsOpening(true)
  //   setButtonVisible(false)
  // }

  // const buttonClosingForm = () => {
  //   setIsOpening(false)
  //   setButtonVisible(true)
  //   setExpenseButtonUnvisible(true)
  // }

  // let allFormData;
  // if(isClosed) {
    
  

  return (
    <form onSubmit = {submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>title</label>
          <input value={enteredTitle} type="text" onChange={titleChangHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={AmountChangeHandler}
            value={enteredAmount}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2023-01-01"
            max="2024-12-31"
            onChange={DatechangeHandler}
            value={enteredDate}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="button" >Cancel</button>
        <button type="submit" onClick={buttonClosingForm} >Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
