import './Expenses.css'
import React, { useState } from "react";
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter';


const Expenses = (props) => {

    const [filteredYear, setFilteredYear] = useState("2020")

    

    const savedYearDataHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
        if(selectedYear === "2019") {
            setFilterInfoText('2020, 2021 & 2022')
        } else if(selectedYear === "2020") {
            setFilterInfoText('2019, 2021 & 2022')
        } else if(selectedYear === "2021") {
            setFilterInfoText('2019, 2020 & 2022')
        } else if(selectedYear === "2022") {
            setFilterInfoText('2019, 2021 & 2020')
        }
    }



    return (
        <div>
            
            <Card className='expenses'>
            <ExpensesFilter selected={filteredYear} forwardedYearData={savedYearDataHandler}/>
                <p>Data for years {filterInfoText} is hidden.</p>
                <ExpenseItem 
                 title={props.expenses[0].title}
                 amount={props.expenses[0].amount}
                 date={props.expenses[0].date}
                ></ExpenseItem>
                <ExpenseItem
                title={props.expenses[1].title}
                amount={props.expenses[1].amount}
                date={props.expenses[1].date}></ExpenseItem>
                <ExpenseItem
                title={props.expenses[2].title}
                amount={props.expenses[2].amount}
                date={props.expenses[2].date}></ExpenseItem>
                <ExpenseItem
                title={props.expenses[3].title}
                amount={props.expenses[3].amount}
                date={props.expenses[3].date}></ExpenseItem>
        
            </Card> 
        </div>
    )
}


export default Expenses;
