import './Expenses.css'
import React, { useState } from "react";
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter';


const Expenses = (props) => {




    const savedYearDataHandler = (chosenYearData) => {
        const yearData = {
            ...chosenYearData,
         }
        const retrievedYears = yearData
    }

    const [retrieveYears, setChosenYear] = useState(savedYearDataHandler())

    



    return (
        <div>
            <ExpensesFilter forwardedYearData={savedYearDataHandler}/>
            <Card className='expenses'>
                <ExpenseItem 
                 title={props.expenses[0].title}
                 amount={props.expenses[0].amount}
                 date={props.expenses[0].date}
                ></ExpenseItem>
                <ExpenseItem
                title={props.expenses[1].title}
                amount={props.expenses[1].amount}
                date={props.expenses[1].date}></ExpenseItem>
                <ExpenseItem
                title={props.expenses[2].title}
                amount={props.expenses[2].amount}
                date={props.expenses[2].date}></ExpenseItem>
                <ExpenseItem
                title={props.expenses[3].title}
                amount={props.expenses[3].amount}
                date={props.expenses[3].date}></ExpenseItem>
        
            </Card> 
        </div>
    )
}


export default Expenses;
